import React from "react";
import Header from "./Header";
import Memo from "./Memo";
import MemoEditor from "./MemoEditor";

class Main extends React.Component {

    state = {
        memos: [],
        viewMemoId: -1,
        adding: false
    }

    showMemoEditor = () => {
        this.setState({
            adding: true
        })
    }

    viewContent = (id) => {
        this.setState({
            viewMemoId: id
        })
    }

    addNewMemo = memo => {
        this.setState({
            memos: [memo, ...this.state.memos]
        })
    }

    cancelAddingMemo = () => {
        this.setState({
            adding: false
        })
    }

    render() {
        return (
            <div className="Main">
                <Header username={this.props.username} />
                <div className="ov">
                    <div className={this.state.adding ? "memos memos-when-adding" : "memos"}>
                        {
                            this.state.memos.map(memo => (
                                <Memo key={memo.id} memo={memo} viewContent={this.viewContent} showContent={memo.id === this.state.viewMemoId} />
                            ))
                        }
                    </div>
                    {this.state.adding ? <div className="fr"><MemoEditor addNewMemo={this.addNewMemo}  /></div> : null}
                </div>
                {this.state.adding ? null : <div className="add-new" onClick={this.showMemoEditor}>+</div>}
            </div>
        )
    }

    componentDidMount() {
        const memos = [{
            id: 1,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 2,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }, {
            id: 3,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 4,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }, {
            id: 5,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 6,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }, {
            id: 7,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 8,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }, {
            id: 9,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 10,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }]
        this.setState({memos})
    }

}

export default Main
