import React from "react";

class MemoEditor extends React.Component {

    state = {
        title: '',
        content: ''
    }

    changeTitle = e => {
        this.setState({
            title: e.target.value
        })
    }

    changeContent = e => {
        this.setState({
            content: e.target.value
        })
    }

    render() {
        return (
            <div className="memo-editor">
                <input value={this.state.title} onChange={this.changeTitle} type="text" className="title" />
                <br />
                <textarea value={this.state.content} onChange={this.changeContent} className="content" />
                <br />
                <button className="submit">Create</button>
                <button className="cancel">Cancel</button>
            </div>
        )
    }

}

export default MemoEditor
