import React from "react";
import Header from "./Header";
import Memo from "./Memo";

class Main extends React.Component {

    state = {
        memos: [],
        viewMemoId: -1
    }

    addNewMemo = () => {
        console.log("Add new memo");
    }

    viewContent = (id) => {
        this.setState({
            viewMemoId: id
        })
    }

    render() {
        return (
            <div className="Main">
                <Header username={this.props.username} />
                <div className="memos">
                    {
                        this.state.memos.map(memo => (
                            <Memo key={memo.id} memo={memo} viewContent={this.viewContent} showContent={memo.id===this.state.view} />
                        ))
                    }
                </div>
                <div className="add-new" onClick={this.addNewMemo}>+</div>
            </div>
        )
    }

    componentDidMount() {
        const memos = [{
            id: 1,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 2,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }]
        this.setState({memos})
    }

}

export default Main
