import React from "react";
import Header from "./Header";
import Memo from "./Memo";

class Main extends React.Component {
    state = {
        memos: []
    }

    componentDidMount() {
        const memos = [{
            id: 1,
            title: "First",
            date: new Date(2019, 0, 19),
            content: "This is the first memo"
        }, {
            id: 2,
            title: 'Second',
            date: new Date(2020, 0, 1),
            content: "This is the second memo"
        }]
        this.setState({memos})
    }

    render() {
        return (
            <div className="Main">
                <Header username={this.props.username} />
                <div className="memos">
                    {
                        this.state.memos.map(memo => (<Memo title={memo.title} date={memo.date} />))
                    }
                </div>
            </div>
        )
    }
}

export default Main
