import React from "react";

class Memo extends React.Component {

    formateDate(date) {
        return `${date.getFullYear()}-${date.getMonth() + 1}-${date.getDate()}`
    }

    viewContent = () => {
        console.log(`view [${this.props.title}]`);
    }

    render() {
        return (
            <div className="single-memo ov" onClick={this.viewContent}>
                <div>
                    <div className="memo-title fl">{this.props.memo.title}</div>
                    <div className="memo-date fr">
                        {this.formateDate(this.props.memo.date)}
                    </div>
                </div>
                <div className="memo-content"></div>
            </div>
        )
    }
    
}

export default Memo
