import React from "react";

class MemoEditor extends React.Component {

    state = {
        title: '',
        content: ''
    }

    changeTitle = e => {
        this.setState({
            title: e.target.value
        })
    }

    changeContent = e => {
        this.setState({
            content: e.target.value
        })
    }

    render() {
        return (
            <div className="memo-editor">
                <input value={this.state.title}  type="text" className="title" />
                <textarea value={this.state.content} className="content" />
                <div className="submit">Create</div>
                <div className="cancel">Cancel</div>
            </div>
        )
    }

}

export default MemoEditor
