import React from "react";

class Login extends React.Component {

    state = {
        username: '',
        password: ''
    }

    changeUsername = e => {
        this.setState({
            username: e.target.value
        })
    }

    changePassword = e => {
        this.setState({
            password: e.target.value
        })
    }

    login = () => {
        // this.props.login(this.state.username)
        fetch('http://localhost:9000/login', {
            method: 'POST',
            body: {
                username: this.state.username
            }
        })
        .then(res => {
            res = res.json()
            return res
        })
        .then(res => {
            console.log(res);
        })
    }

    render() {
        return (
            <div className="login">
                <div className="title">MEMO</div>
                <div className="wrapper">
                    <label htmlFor="username">Username: </label>
                    <input type="text" id="username" value={this.state.username} onChange={this.changeUsername} />
                </div>
                <div className="wrapper">
                    <label htmlFor="password">Password: </label>
                    <input type="password" id="password" value={this.state.password} onChange={this.changePassword} />
                </div>
                <div className="login-btn btn" onClick={this.login}>Login</div>
                <br />
                <div className="btn">Sign up</div>
            </div>
        )
    }
    
}

export default Login
